package com.sj.plant.ui.knowledge

import androidx.lifecycle.MutableLiveData
import androidx.lifecycle.ViewModel
import com.sj.plant.R
import com.sj.plant.ai.AnalysisData
import com.sj.plant.ai.LoadModelTask

class KnowledgeViewModel : ViewModel() {

    fun search() {
        listKnowledge.clear()
        val data = analysisData.value
        if (data == null) {
            ALL.forEach {
                listKnowledge.add(it)
            }
        } else {
            ALL.forEach {
                if (it.category == data.category) {
                    listKnowledge.add(it)
                }
            }
        }
    }

    fun searchByName(name: String) {
        listKnowledge.clear()
        LoadModelTask.getInstance().labels.let { it ->
            for (i in it.indices) {
                it[i]?.let { it1 ->
                    if (it1.contains(name)) {
                        ALL.forEach {
                            if (it.category == i) {
                                listKnowledge.add(it)
                            }
                        }
                        return
                    }
                }
            }
        }
    }

    val analysisData: MutableLiveData<AnalysisData> = MutableLiveData()
    val listKnowledge = mutableListOf<ContentData>()
    var knowledgeSelect: ContentData? = null

    companion object {
        data class ContentData(
            var category: Int,
            var title: String,
            internal var detail: String,
            internal var contentText: String,
            val image: Int
        )










































